import React, {useEffect, useState} from 'react'

const PrintQuestion = (props) => {
    const [totalQuestion, setTotalQuestion] = useState([])

    useEffect(() => {
        let index = 0
        let stockQuestion = []
        const questionPrint = setInterval(() => {
            stockQuestion = [...stockQuestion, props.question[index]]
            setTotalQuestion(stockQuestion)
            index++
            if(index === props.question.length){
                clearInterval(questionPrint)
            }
        }, 50)

        if(index !== props.question.length){
            setTimeout(() => {
                stockQuestion = [...stockQuestion, props.question[index]]
                setTotalQuestion(stockQuestion)
                index++
            })
        }
    }, [props.question])

    return(
        <p className="textCardQuestChat">{totalQuestion}</p>
    )
}

export default PrintQuestion
